mapper.select:
SELECT
       A.PWD
  FROM TBL_MEMBER_ALIVE A
 WHERE A.ID = #{ id }
   AND A.PHONE1 = #{ phone1 }
   AND A.PHONE2 = #{ phone2 }
   AND A.PHONE3 = #{ phone3 }

mapper.update:
UPDATE
	   TBL_MEMBER_ALIVE A
   SET A.PWD = #{ pwd }
 WHERE A.ID = #{ id }

mapper.select:
		SELECT
		       A.ID
		  FROM TBL_MEMBER_ALIVE A
		 WHERE A.NAME = #{ name }
 		   AND A.ID_QUE_NUM = #{ idQueNum } 
		   AND A.ID_ANSWER = #{ idAnswer } 
		   AND A.USER_STATUS = 'A'

mapper.insert:
INSERT
  INTO TBL_MEMBER_DEAD A
(
  A.NO
, A.PHONE1
, A.PHONE2
, A.PHONE3
, A.EMAIL1
, A.EMAIL2
, A.ADDRESS1
, A.ADDRESS2
, A.ADDRESS3
, A.ID
, A.PWD
, A.BIRTH
, A.NAME
, A.GENDER  
)  
VALUES
(
  CONCAT('D-', SEQ_MEMBER_DEAD_NO.NEXTVAL)	
, #{ phone1 }
, #{ phone2 }
, #{ phone3 }
, #{ email1 }
, #{ email2 }
, #{ address1 }
, #{ address2 }
, #{ address3 }
, #{ id }
, #{ pwd }
, #{ birth }
, #{ name }  
, #{ gender }
)
